import React from 'react';
import styled from 'styled-components';

interface Settings {
  threshold: number;
  lowVolumeDuration: number;
  textAlerts: boolean;
  soundAlerts: boolean;
  alertVolume: number;
}

interface SettingsPanelProps {
  isOpen: boolean;
  onClose: () => void;
  settings: Settings;
  onSettingsChange: (newSettings: Partial<Settings>) => void;
}

const Overlay = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  opacity: ${props => props.isOpen ? 1 : 0};
  visibility: ${props => props.isOpen ? 'visible' : 'hidden'};
  transition: all 0.3s;
  z-index: 150;
`;

const Panel = styled.div<{ isOpen: boolean }>`
  position: fixed;
  right: ${props => props.isOpen ? '0' : '-400px'};
  top: 0;
  bottom: 0;
  width: 400px;
  background: white;
  box-shadow: -2px 0 8px rgba(0, 0, 0, 0.1);
  padding: 24px;
  transition: right 0.3s;
  z-index: 151;
  overflow-y: auto;
`;

const Title = styled.h2`
  margin: 0 0 24px 0;
  font-size: 20px;
  color: #333;
  padding-bottom: 16px;
  border-bottom: 1px solid #eee;
`;

const Section = styled.div`
  margin-bottom: 32px;
`;

const SectionTitle = styled.h3`
  font-size: 16px;
  color: #666;
  margin: 0 0 16px 0;
`;

const SettingItem = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 8px;
  color: #666;
  font-size: 14px;
`;

const Slider = styled.input`
  width: 100%;
  margin: 8px 0;
`;

const Toggle = styled.label`
  display: flex;
  align-items: center;
  cursor: pointer;
  user-select: none;
  color: #666;
`;

const ToggleInput = styled.input`
  margin-right: 8px;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 24px;
  right: 24px;
  background: none;
  border: none;
  color: #666;
  cursor: pointer;
  padding: 8px;
  
  &:hover {
    color: #333;
  }
`;

export const SettingsPanel: React.FC<SettingsPanelProps> = ({
  isOpen,
  onClose,
  settings,
  onSettingsChange
}) => {
  return (
    <>
      <Overlay isOpen={isOpen} onClick={onClose} />
      <Panel isOpen={isOpen}>
        <Title>设置</Title>
        <CloseButton onClick={onClose}>✕</CloseButton>

        <Section>
          <SectionTitle>音量监控</SectionTitle>
          <SettingItem>
            <Label>
              <span>音量阈值</span>
              <span>{settings.threshold}%</span>
            </Label>
            <Slider
              type="range"
              min="0"
              max="100"
              value={settings.threshold}
              onChange={(e) => onSettingsChange({ threshold: Number(e.target.value) })}
            />
          </SettingItem>
          
          <SettingItem>
            <Label>
              <span>低音量持续时间</span>
              <span>{(settings.lowVolumeDuration / 1000).toFixed(1)}秒</span>
            </Label>
            <Slider
              type="range"
              min="1000"
              max="10000"
              step="500"
              value={settings.lowVolumeDuration}
              onChange={(e) => onSettingsChange({ lowVolumeDuration: Number(e.target.value) })}
            />
          </SettingItem>
        </Section>

        <Section>
          <SectionTitle>提示设置</SectionTitle>
          <SettingItem>
            <Toggle>
              <ToggleInput
                type="checkbox"
                checked={settings.textAlerts}
                onChange={(e) => onSettingsChange({ textAlerts: e.target.checked })}
              />
              启用文字提示
            </Toggle>
          </SettingItem>
          
          <SettingItem>
            <Toggle>
              <ToggleInput
                type="checkbox"
                checked={settings.soundAlerts}
                onChange={(e) => onSettingsChange({ soundAlerts: e.target.checked })}
              />
              启用声音提示
            </Toggle>
          </SettingItem>
          
          <SettingItem>
            <Label>
              <span>提示音量</span>
              <span>{Math.round(settings.alertVolume * 100)}%</span>
            </Label>
            <Slider
              type="range"
              min="0"
              max="100"
              value={settings.alertVolume * 100}
              onChange={(e) => onSettingsChange({ alertVolume: Number(e.target.value) / 100 })}
            />
          </SettingItem>
        </Section>
      </Panel>
    </>
  );
};
