import React from 'react';
import styled from 'styled-components';
import { Settings } from '../types';
import { alertManager } from '../utils/alertManager';

interface SettingsPanelProps {
  settings: {
    threshold: number;
    lowVolumeDuration: number;
    textAlerts: boolean;
    soundAlerts: boolean;
    alertVolume: number;
  };
  onSettingsChange: (newSettings: Partial<Settings>) => void;
}

const Panel = styled.div`
  position: fixed;
  left: 0;
  top: 0;
  bottom: 0;
  width: 280px;
  background: white;
  box-shadow: 2px 0 8px rgba(0,0,0,0.1);
  padding: 20px;
  overflow-y: auto;
  z-index: 100;
`;

const Title = styled.h3`
  margin: 0 0 24px 0;
  font-size: 16px;
  color: #333;
  padding-bottom: 12px;
  border-bottom: 1px solid #eee;
`;

const Section = styled.div`
  margin-bottom: 32px;
`;

const SectionTitle = styled.h4`
  margin: 0 0 16px 0;
  font-size: 14px;
  color: #666;
`;

const SettingItem = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 8px;
  color: #666;
  font-size: 14px;
`;

const Slider = styled.input`
  width: 100%;
  margin: 8px 0;
`;

const Toggle = styled.label`
  display: flex;
  align-items: center;
  cursor: pointer;
  user-select: none;
  
  input {
    margin-right: 8px;
  }
`;

const TestButton = styled.button`
  margin-top: 8px;
  padding: 6px 12px;
  background-color: #2196f3;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 14px;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #1976d2;
  }

  &:active {
    background-color: #1565c0;
  }
`;

export const SettingsPanel: React.FC<SettingsPanelProps> = ({
  settings,
  onSettingsChange
}) => {
  const handleThresholdChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = Number(e.target.value);
    console.log('Threshold changing to:', newValue);
    onSettingsChange({ threshold: newValue });
  };

  const handleDurationChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = Number(e.target.value);
    console.log('Duration changing to:', newValue);
    onSettingsChange({ lowVolumeDuration: newValue });
  };

  return (
    <Panel>
      <Title>音量监控设置</Title>

      <Section>
        <SectionTitle>提醒设置</SectionTitle>
        <SettingItem>
          <Toggle>
            <input
              type="checkbox"
              checked={settings.soundAlerts}
              onChange={(e) => onSettingsChange({ soundAlerts: e.target.checked })}
            />
            启用声音提醒
          </Toggle>
        </SettingItem>

        {settings.soundAlerts && (
          <SettingItem>
            <Label>
              <span>提示音量</span>
              <span>{Math.round(settings.alertVolume * 100)}%</span>
            </Label>
            <Slider
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={settings.alertVolume}
              onChange={(e) => onSettingsChange({ 
                alertVolume: Number(e.target.value) 
              })}
            />
          </SettingItem>
        )}
      </Section>

      <Section>
        <SectionTitle>音量监控</SectionTitle>
        <SettingItem>
          <Label>
            <span>建议说话音量</span>
            <span>{settings.threshold} dB</span>
          </Label>
          <Slider
            type="range"
            min="-60"
            max="-10"
            step="1"
            value={settings.threshold}
            onChange={handleThresholdChange}
          />
        </SettingItem>
        
        <SettingItem>
          <Label>
            <span>低音量持续时间</span>
            <span>{(settings.lowVolumeDuration / 1000).toFixed(1)}秒</span>
          </Label>
          <Slider
            type="range"
            min="1000"
            max="10000"
            step="500"
            value={settings.lowVolumeDuration}
            onChange={handleDurationChange}
          />
        </SettingItem>
      </Section>
    </Panel>
  );
};
