import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  background-color: rgba(0, 0, 0, 0.9);
  padding: 20px;
  border-radius: 8px;
  color: white;
  font-family: monospace;
  z-index: 1000;
  min-width: 300px;
`;

const Title = styled.div`
  font-weight: bold;
  margin-bottom: 15px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Section = styled.div`
  margin-bottom: 15px;
  padding-bottom: 15px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);

  &:last-child {
    margin-bottom: 0;
    padding-bottom: 0;
    border-bottom: none;
  }
`;

const SectionTitle = styled.div`
  font-size: 12px;
  color: #888;
  margin-bottom: 8px;
`;

const ButtonGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 8px;
`;

const Button = styled.button<{ variant?: 'primary' | 'warning' | 'danger' }>`
  background-color: ${props => {
    switch (props.variant) {
      case 'warning': return '#ff9800';
      case 'danger': return '#f44336';
      case 'primary': return '#2196f3';
      default: return '#424242';
    }
  }};
  color: white;
  border: none;
  padding: 8px 12px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 12px;
  transition: opacity 0.2s;

  &:hover {
    opacity: 0.8;
  }
`;

const MonitoringInfo = styled.div`
  font-size: 12px;
  color: #888;
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

interface TestPanelProps {
  onTest: (type: string, params?: any) => void;
  currentVolume?: number;
  threshold?: number;
}

export const TestPanel: React.FC<TestPanelProps> = ({
  onTest,
  currentVolume = 0,
  threshold = 30
}) => {
  const [isVisible, setIsVisible] = useState(true);
  const [isRunningTest, setIsRunningTest] = useState(false);

  const runTimedTest = async (type: string, duration: number = 5000) => {
    setIsRunningTest(true);
    onTest(type);
    await new Promise(resolve => setTimeout(resolve, duration));
    setIsRunningTest(false);
  };

  if (!isVisible) {
    return (
      <Button 
        style={{ position: 'fixed', bottom: 20, right: 20 }}
        onClick={() => setIsVisible(true)}
      >
        显示测试面板
      </Button>
    );
  }

  return (
    <Container>
      <Title>
        测试控制面板
        <Button onClick={() => setIsVisible(false)}>
          隐藏
        </Button>
      </Title>

      <Section>
        <SectionTitle>音量测试</SectionTitle>
        <ButtonGrid>
          <Button 
            variant="warning"
            onClick={() => runTimedTest('lowVolume')}
            disabled={isRunningTest}
          >
            持续低音量 (5s)
          </Button>
          <Button 
            onClick={() => runTimedTest('normalVolume')}
            disabled={isRunningTest}
          >
            正常音量
          </Button>
          <Button 
            variant="danger"
            onClick={() => runTimedTest('volumeSpike')}
            disabled={isRunningTest}
          >
            音量突变
          </Button>
          <Button 
            variant="warning"
            onClick={() => runTimedTest('volumeDrop')}
            disabled={isRunningTest}
          >
            音量骤降
          </Button>
        </ButtonGrid>
      </Section>

      <Section>
        <SectionTitle>提示测试</SectionTitle>
        <ButtonGrid>
          <Button 
            variant="primary"
            onClick={() => onTest('visualAlert')}
          >
            视觉提示
          </Button>
          <Button 
            variant="primary"
            onClick={() => onTest('textAlert')}
          >
            文字提示
          </Button>
          <Button 
            variant="primary"
            onClick={() => onTest('soundAlert')}
          >
            声音提示
          </Button>
          <Button 
            variant="danger"
            onClick={() => onTest('allAlerts')}
          >
            全部提示
          </Button>
        </ButtonGrid>
      </Section>

      <Section>
        <SectionTitle>异常测试</SectionTitle>
        <ButtonGrid>
          <Button 
            variant="danger"
            onClick={() => onTest('microphoneError')}
          >
            麦克风错误
          </Button>
          <Button 
            variant="danger"
            onClick={() => onTest('connectionError')}
          >
            连接错误
          </Button>
        </ButtonGrid>
      </Section>

      <MonitoringInfo>
        <div>当前音量: {currentVolume.toFixed(1)}%</div>
        <div>阈值设置: {threshold}%</div>
        <div>状态: {isRunningTest ? '测试运行中...' : '就绪'}</div>
      </MonitoringInfo>
    </Container>
  );
};
