import React from 'react';
import styled from 'styled-components';
import { AnalysisResult } from '../utils/volumeAnalyzer';

interface VolumeAnalysisProps {
  analysis: AnalysisResult;
  isCompact?: boolean;
}

const Container = styled.div<{ isCompact: boolean }>`
  background-color: #f5f5f5;
  padding: ${props => props.isCompact ? '10px' : '20px'};
  border-radius: 8px;
  margin-top: 20px;
  width: ${props => props.isCompact ? '200px' : '300px'};
`;

const StatRow = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 8px;
  font-size: 14px;
`;

const Label = styled.span`
  color: #666;
`;

const Value = styled.span`
  font-weight: 500;
  color: #333;
`;

const Recommendation = styled.div<{ trend: string }>`
  margin-top: 12px;
  padding: 8px;
  border-radius: 4px;
  background-color: ${props => {
    switch (props.trend) {
      case 'rising': return '#e8f5e9';
      case 'falling': return '#ffebee';
      default: return '#e3f2fd';
    }
  }};
  color: ${props => {
    switch (props.trend) {
      case 'rising': return '#2e7d32';
      case 'falling': return '#c62828';
      default: return '#1565c0';
    }
  }};
  font-size: 14px;
`;

export const VolumeAnalysis: React.FC<VolumeAnalysisProps> = ({
  analysis,
  isCompact = false
}) => {
  const { currentStats, trend, recommendation } = analysis;

  return (
    <Container isCompact={isCompact}>
      {!isCompact && (
        <>
          <StatRow>
            <Label>平均音量:</Label>
            <Value>{currentStats.average.toFixed(1)}%</Value>
          </StatRow>
          <StatRow>
            <Label>最大音量:</Label>
            <Value>{currentStats.max.toFixed(1)}%</Value>
          </StatRow>
          <StatRow>
            <Label>最小音量:</Label>
            <Value>{currentStats.min.toFixed(1)}%</Value>
          </StatRow>
          <StatRow>
            <Label>音量趋势:</Label>
            <Value>
              {trend === 'rising' && '上升'}
              {trend === 'falling' && '下降'}
              {trend === 'stable' && '稳定'}
            </Value>
          </StatRow>
        </>
      )}
      {recommendation && (
        <Recommendation trend={trend}>
          {recommendation}
        </Recommendation>
      )}
    </Container>
  );
};
