import React, { useEffect, useState } from 'react';
import styled, { keyframes, css } from 'styled-components';

interface AlertNotificationProps {
  isVisible: boolean;
  message: string;
  type?: 'warning' | 'error' | 'info';
  duration?: number;
  onClose?: () => void;
  showIcon?: boolean;
  showProgress?: boolean;
}

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(-20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const fadeOut = keyframes`
  from { opacity: 1; transform: translateY(0); }
  to { opacity: 0; transform: translateY(-20px); }
`;

const pulseAnimation = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const Container = styled.div<{ isVisible: boolean; type: string }>`
  position: fixed;
  top: 20px;
  left: 50%;
  transform: translateX(-50%);
  padding: 12px 24px;
  border-radius: 8px;
  background-color: ${props => {
    switch (props.type) {
      case 'error': return 'rgba(244, 67, 54, 0.95)';
      case 'warning': return 'rgba(255, 152, 0, 0.95)';
      default: return 'rgba(33, 150, 243, 0.95)';
    }
  }};
  color: white;
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.15);
  z-index: 1000;
  animation: ${props => props.isVisible ? fadeIn : fadeOut} 0.3s ease-in-out;
  pointer-events: ${props => props.isVisible ? 'auto' : 'none'};
  ${props => props.type === 'warning' && props.isVisible && css`
    animation: ${pulseAnimation} 2s infinite;
  `}
`;

const IconContainer = styled.div<{ type: string }>`
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  
  svg {
    width: 20px;
    height: 20px;
    fill: currentColor;
  }
`;

const ProgressBar = styled.div<{ duration: number }>`
  position: absolute;
  bottom: 0;
  left: 0;
  height: 2px;
  background-color: rgba(255, 255, 255, 0.5);
  width: 100%;
  transform-origin: left;
  animation: ${props => css`
    ${shrinkAnimation} ${props.duration}ms linear
  `};
`;

export const AlertNotification: React.FC<AlertNotificationProps> = ({
  isVisible,
  message,
  type,
  duration = 3000,
  onClose,
  showIcon = true,
  showProgress = true,
}) => {
  const [isExiting, setIsExiting] = useState(false);

  useEffect(() => {
    if (!isVisible) {
      setIsExiting(false);
      return;
    }

    if (duration > 0) {
      const timer = setTimeout(() => {
        setIsExiting(true);
        setTimeout(onClose, 300);
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [isVisible, duration, onClose]);

  const getIcon = () => {
    switch (type) {
      case 'warning':
        return <WarningIcon />;
      case 'error':
        return <ErrorIcon />;
      case 'info':
        return <InfoIcon />;
      default:
        return null;
    }
  };

  if (!isVisible && !isExiting) return null;

  return (
    <Container
      isVisible={isVisible}
      type={type}
      isExiting={isExiting}
      onClick={onClose}
    >
      {showIcon && <IconContainer type={type}>{getIcon()}</IconContainer>}
      <Message>{message}</Message>
      <CloseButton onClick={(e) => {
        e.stopPropagation();
        setIsExiting(true);
        setTimeout(onClose, 300);
      }}>
        âœ•
      </CloseButton>
      {showProgress && <ProgressBar duration={duration} />}
    </Container>
  );
};
