import React from 'react';
import styled from 'styled-components';
import { Settings } from '../types';

interface SettingsPanelProps {
  settings: {
    threshold: number;
    lowVolumeDuration: number;
    textAlerts: boolean;
    soundAlerts: boolean;
    alertVolume: number;
  };
  onSettingsChange: (newSettings: Partial<Settings>) => void;
}

const Panel = styled.div`
  position: fixed;
  left: 0;
  top: 0;
  bottom: 0;
  width: 280px;
  background: white;
  box-shadow: 2px 0 8px rgba(0,0,0,0.1);
  padding: 20px;
  overflow-y: auto;
  z-index: 100;
`;

const Title = styled.h3`
  margin: 0 0 24px 0;
  font-size: 16px;
  color: #333;
  padding-bottom: 12px;
  border-bottom: 1px solid #eee;
`;

const Section = styled.div`
  margin-bottom: 32px;
`;

const SectionTitle = styled.h4`
  margin: 0 0 16px 0;
  font-size: 14px;
  color: #666;
`;

const SettingItem = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 8px;
  color: #666;
  font-size: 14px;
`;

const Slider = styled.input`
  width: 100%;
  margin: 8px 0;
`;

const Toggle = styled.label`
  display: flex;
  align-items: center;
  cursor: pointer;
  user-select: none;
  
  input {
    margin-right: 8px;
  }
`;

export const SettingsPanel: React.FC<SettingsPanelProps> = ({
  settings,
  onSettingsChange
}) => {
  return (
    <Panel>
      <Title>音量监控设置</Title>

      <Section>
        <SectionTitle>音量监控</SectionTitle>
        <SettingItem>
          <Label>
            <span>音量阈值</span>
            <span>{settings.threshold}%</span>
          </Label>
          <Slider
            type="range"
            min="0"
            max="100"
            value={settings.threshold}
            onChange={(e) => onSettingsChange({ threshold: Number(e.target.value) })}
          />
        </SettingItem>
        
        <SettingItem>
          <Label>
            <span>低音量持续时间</span>
            <span>{(settings.lowVolumeDuration / 1000).toFixed(1)}秒</span>
          </Label>
          <Slider
            type="range"
            min="1000"
            max="10000"
            step="500"
            value={settings.lowVolumeDuration}
            onChange={(e) => onSettingsChange({ lowVolumeDuration: Number(e.target.value) })}
          />
        </SettingItem>
      </Section>

    </Panel>
  );
};
