import { Settings } from '../types';

interface AlertSettings {
  soundAlerts: boolean;
  textAlerts: boolean;
  alertVolume: number;
  threshold: number;
  lowVolumeDuration: number;
  alertInterval: number;
}

class AlertManager {
  private audioContext: AudioContext | null = null;
  private oscillator: OscillatorNode | null = null;
  private gainNode: GainNode | null = null;
  private settings: AlertSettings = {
    soundAlerts: true,
    textAlerts: true,
    alertVolume: 0.5,
    threshold: 30,
    lowVolumeDuration: 3000,
    alertInterval: 1000
  };

  private async initAudio() {
    if (this.audioContext) return;

    try {
      this.audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      
      this.oscillator = this.audioContext.createOscillator();
      this.oscillator.type = 'sine';
      this.oscillator.frequency.setValueAtTime(440, this.audioContext.currentTime);
      
      this.gainNode = this.audioContext.createGain();
      this.gainNode.gain.setValueAtTime(0, this.audioContext.currentTime);
      
      this.oscillator.connect(this.gainNode);
      this.gainNode.connect(this.audioContext.destination);
      
      this.oscillator.start();
    } catch (error) {
      console.error('Failed to initialize audio:', error);
      this.cleanup();
    }
  }

  private async playSound() {
    if (!this.settings.soundAlerts) return;

    try {
      await this.initAudio();
      if (this.gainNode && this.audioContext) {
        const now = this.audioContext.currentTime;
        
        this.gainNode.gain.cancelScheduledValues(now);
        this.gainNode.gain.setValueAtTime(0, now);
        this.gainNode.gain.linearRampToValueAtTime(this.settings.alertVolume, now + 0.01);
        this.gainNode.gain.linearRampToValueAtTime(0, now + 0.5);
      }
    } catch (error) {
      console.error('Failed to play alert sound:', error);
    }
  }

  trigger(type: 'sound' | 'text' | 'visual' | 'all' | 'low-volume') {
    switch (type) {
      case 'sound':
      case 'low-volume':
      case 'all':
        this.playSound();
        break;
    }
  }

  updateConfig(settings: Partial<AlertSettings>) {
    this.settings = { ...this.settings, ...settings };
  }

  getSettings(): AlertSettings {
    return { ...this.settings };
  }

  private cleanup() {
    if (this.oscillator) {
      this.oscillator.stop();
      this.oscillator.disconnect();
    }
    if (this.gainNode) {
      this.gainNode.disconnect();
    }
    if (this.audioContext) {
      this.audioContext.close();
    }
    this.oscillator = null;
    this.gainNode = null;
    this.audioContext = null;
  }

  dispose() {
    this.cleanup();
  }
}

export const alertManager = new AlertManager();
