import React from 'react';
import styled from 'styled-components';

interface SideMenuProps {
  onOpenSettings: () => void;
}

const MenuContainer = styled.div`
  position: fixed;
  left: 0;
  top: 0;
  bottom: 0;
  width: 240px;
  background: #fff;
  box-shadow: 2px 0 8px rgba(0, 0, 0, 0.1);
  padding: 20px;
  display: flex;
  flex-direction: column;
  z-index: 100;
`;

const Logo = styled.div`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 32px;
  padding-bottom: 16px;
  border-bottom: 1px solid #eee;
`;

const MenuItem = styled.button`
  display: flex;
  align-items: center;
  padding: 12px 16px;
  margin-bottom: 8px;
  border: none;
  background: transparent;
  color: #666;
  font-size: 14px;
  cursor: pointer;
  border-radius: 4px;
  transition: all 0.2s;

  &:hover {
    background: #f5f5f5;
    color: #2196f3;
  }

  svg {
    margin-right: 12px;
    width: 20px;
    height: 20px;
  }
`;

export const SideMenu: React.FC<SideMenuProps> = ({
  onOpenSettings
}) => {
  return (
    <MenuContainer>
      <Logo>音量监控</Logo>
      
      <MenuItem onClick={onOpenSettings}>
        <svg viewBox="0 0 24 24" fill="currentColor">
