import { useState, useEffect, useCallback, useRef } from 'react';

interface AudioMonitorProps {
  threshold: number;
  lowVolumeDuration: number;
  onVolumeLow: () => void;
  onLowVolumeDurationUpdate: (duration: number) => void;
}

export const useAudioMonitor = ({
  threshold,
  lowVolumeDuration,
  onVolumeLow,
  onLowVolumeDurationUpdate,
}: AudioMonitorProps) => {
  const [volume, setVolume] = useState(0);
  const [error, setError] = useState<string | null>(null);
  const audioContextRef = useRef<AudioContext | null>(null);
  const analyserRef = useRef<AnalyserNode | null>(null);
  const sourceRef = useRef<MediaStreamAudioSourceNode | null>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const isRunningRef = useRef(false);
  const lowVolumeStartTimeRef = useRef<number | null>(null);
  const currentDurationRef = useRef<number>(lowVolumeDuration);

  useEffect(() => {
    currentDurationRef.current = lowVolumeDuration;
  }, [lowVolumeDuration]);

  const analyze = useCallback(() => {
    if (!analyserRef.current || !isRunningRef.current) return;

    const dataArray = new Uint8Array(analyserRef.current.frequencyBinCount);
    analyserRef.current.getByteFrequencyData(dataArray);

    const average = dataArray.reduce((a, b) => a + b) / dataArray.length;
    const normalizedVolume = Math.round((average / 255) * 100);
    setVolume(normalizedVolume);

    const now = Date.now();
    if (normalizedVolume < threshold) {
      // 如果是新的低音量期间，记录开始时间
      if (lowVolumeStartTimeRef.current === null) {
        lowVolumeStartTimeRef.current = now;
      }
      
      // 计算持续时间
      const duration = now - lowVolumeStartTimeRef.current;
      onLowVolumeDurationUpdate(duration);

      // 严格判断：只有当持续时间刚好达到配置时间时触发提醒
      if (Math.floor(duration / 1000) * 1000 === currentDurationRef.current) {
        onVolumeLow();
        // 重置计时器，开始新的计时周期
        lowVolumeStartTimeRef.current = now;
        onLowVolumeDurationUpdate(0);
      }
    } else {
      // 音量恢复正常时重置计时器
      lowVolumeStartTimeRef.current = null;
      onLowVolumeDurationUpdate(0);
    }

    setTimeout(analyze, 100);
  }, [threshold, onVolumeLow, onLowVolumeDurationUpdate]);

  const stopMonitoring = useCallback(() => {
    isRunningRef.current = false;
    if (sourceRef.current) {
      sourceRef.current.disconnect();
    }
    if (audioContextRef.current) {
      audioContextRef.current.close();
    }
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
    // 重置计时器
    lowVolumeStartTimeRef.current = null;
    onLowVolumeDurationUpdate(0);
  }, [onLowVolumeDurationUpdate]);

  const startMonitoring = useCallback(async () => {
    try {
      if (!navigator.mediaDevices) {
        throw new Error('浏览器不支持音频输入');
      }

      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      streamRef.current = stream;

      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      audioContextRef.current = audioContext;

      const analyser = audioContext.createAnalyser();
      analyser.fftSize = 256;
      analyserRef.current = analyser;

      const source = audioContext.createMediaStreamSource(stream);
      source.connect(analyser);
      sourceRef.current = source;

      // 重置计时器
      lowVolumeStartTimeRef.current = null;
      isRunningRef.current = true;
      analyze();
    } catch (err) {
      setError(err instanceof Error ? err.message : '未知错误');
    }
  }, [analyze]);

  useEffect(() => {
    return () => {
      stopMonitoring();
    };
  }, [stopMonitoring]);

  return {
    volume,
    error,
    startMonitoring,
    stopMonitoring,
  };
};
