import { useState, useCallback, useRef, useEffect } from 'react';
import { audioContextManager } from '../utils/audioContext';
import { alertManager } from '../utils/alertManager';

interface UseAudioMonitorOptions {
  threshold?: number;
  lowVolumeDuration?: number;
  onVolumeLow?: () => void;
  onLowVolumeDurationUpdate?: (duration: number) => void;
  sampleInterval?: number;
}

export function useAudioMonitor({
  threshold = 30,
  lowVolumeDuration = 3000,
  onVolumeLow,
  onLowVolumeDurationUpdate,
  sampleInterval = 50
}: UseAudioMonitorOptions = {}) {
  const [volume, setVolume] = useState<number>(0);
  const [isMonitoring, setIsMonitoring] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  
  const animationFrameId = useRef<number | null>(null);
  const lastUpdateTime = useRef<number>(0);
  const lowVolumeStartTime = useRef<number | null>(null);
  const hasTriggeredAlert = useRef<boolean>(false);

  const updateVolume = useCallback(() => {
    const now = Date.now();
    if (now - lastUpdateTime.current >= sampleInterval) {
      const currentVolume = audioContextManager.getVolume();
      setVolume(currentVolume);
      lastUpdateTime.current = now;

      // 检查低音量持续时间
      if (currentVolume < threshold) {
        if (lowVolumeStartTime.current === null) {
          // 首次低于阈值，记录开始时间
          lowVolumeStartTime.current = now;
          onLowVolumeDurationUpdate?.(0);
          hasTriggeredAlert.current = false;
        } else {
          const duration = now - lowVolumeStartTime.current;
          
          if (duration >= lowVolumeDuration) {
            if (!hasTriggeredAlert.current) {
              // 触发提示
              onVolumeLow?.();
              alertManager.trigger('all');
              hasTriggeredAlert.current = true;
            }
            // 重置计时器
            lowVolumeStartTime.current = now;
            onLowVolumeDurationUpdate?.(0);
          } else {
            // 只在未达到阈值时更新持续时间
            onLowVolumeDurationUpdate?.(duration);
          }
        }
      } else {
        // 音量恢复正常，重置计时器和提示状态
        if (lowVolumeStartTime.current !== null) {
          lowVolumeStartTime.current = null;
          hasTriggeredAlert.current = false;
          onLowVolumeDurationUpdate?.(0);
        }
      }
    }
    
    animationFrameId.current = requestAnimationFrame(updateVolume);
  }, [threshold, onVolumeLow, sampleInterval, lowVolumeDuration, onLowVolumeDurationUpdate]);

  const startMonitoring = useCallback(async () => {
    try {
      await audioContextManager.startMonitoring();
      lastUpdateTime.current = Date.now();
      updateVolume();
      setIsMonitoring(true);
      setError(null);
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : '未知错误';
      setError(errorMessage);
      setIsMonitoring(false);
    }
  }, [updateVolume]);

  const stopMonitoring = useCallback(() => {
    if (animationFrameId.current) {
      cancelAnimationFrame(animationFrameId.current);
      animationFrameId.current = null;
    }
    audioContextManager.stopMonitoring();
    setIsMonitoring(false);
    lowVolumeStartTime.current = null;
    hasTriggeredAlert.current = false;
    onLowVolumeDurationUpdate?.(0);
  }, [onLowVolumeDurationUpdate]);

  useEffect(() => {
    return () => {
      stopMonitoring();
    };
  }, [stopMonitoring]);

  return {
    volume,
    isMonitoring,
    error,
    startMonitoring,
    stopMonitoring
  };
}
