import React from 'react';
import styled from 'styled-components';
import { Settings } from '../utils/settingsManager';

interface SettingsPanelProps {
  isOpen: boolean;
  settings: Settings;
  onSettingsChange: (settings: Partial<Settings>) => void;
  onClose: () => void;
  onReset: () => void;
}

const Drawer = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 0;
  left: ${props => props.isOpen ? '0' : '-320px'};
  width: 320px;
  height: 100vh;
  background-color: white;
  box-shadow: 2px 0 8px rgba(0, 0, 0, 0.15);
  transition: left 0.3s ease-in-out;
  z-index: 1000;
  padding: 20px;
  overflow-y: auto;
`;

const Overlay = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  opacity: ${props => props.isOpen ? 1 : 0};
  visibility: ${props => props.isOpen ? 'visible' : 'hidden'};
  transition: opacity 0.3s ease-in-out;
  z-index: 999;
`;

const Section = styled.section`
  margin-bottom: 24px;
`;

const SectionTitle = styled.h3`
  margin: 0 0 16px;
  font-size: 16px;
  color: #333;
`;

const SettingItem = styled.div`
  margin-bottom: 16px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  color: #666;
`;

const Slider = styled.input`
  width: 100%;
  margin: 8px 0;
`;

const Toggle = styled.label`
  display: flex;
  align-items: center;
  cursor: pointer;
`;

const ToggleInput = styled.input`
  margin-right: 8px;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  margin-top: 8px;
  border: none;
  border-radius: 4px;
  background-color: #f5f5f5;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #e0e0e0;
  }
`;

const ResetButton = styled(Button)`
  background-color: #ffebee;
  color: #f44336;

  &:hover {
    background-color: #ffcdd2;
  }
`;

export const SettingsPanel: React.FC<SettingsPanelProps> = ({
  isOpen,
  settings,
  onSettingsChange,
  onClose,
  onReset
}) => {
  const handleThresholdChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onSettingsChange({ threshold: Number(e.target.value) });
  };

  const handleAlertVolumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onSettingsChange({ alertVolume: Number(e.target.value) / 100 });
  };

  const handleTextAlertToggle = (e: React.ChangeEvent<HTMLInputElement>) => {
    onSettingsChange({ textAlertEnabled: e.target.checked });
  };

  const handleSoundAlertToggle = (e: React.ChangeEvent<HTMLInputElement>) => {
    onSettingsChange({ soundAlertEnabled: e.target.checked });
  };

  return (
    <>
      <Overlay isOpen={isOpen} onClick={onClose} />
      <Drawer isOpen={isOpen}>
        <Section>
          <SectionTitle>音量设置</SectionTitle>
          <SettingItem>
            <Label>音量阈值: {settings.threshold}%</Label>
            <Slider
              type="range"
              min="0"
              max="100"
              value={settings.threshold}
              onChange={handleThresholdChange}
            />
          </SettingItem>
        </Section>

        <Section>
          <SectionTitle>提示设置</SectionTitle>
          <SettingItem>
            <Toggle>
              <ToggleInput
                type="checkbox"
                checked={settings.textAlertEnabled}
                onChange={handleTextAlertToggle}
              />
              启用文字提示
            </Toggle>
          </SettingItem>
          <SettingItem>
            <Toggle>
              <ToggleInput
                type="checkbox"
                checked={settings.soundAlertEnabled}
                onChange={handleSoundAlertToggle}
              />
              启用声音提示
            </Toggle>
          </SettingItem>
          <SettingItem>
            <Label>提示音量: {Math.round(settings.alertVolume * 100)}%</Label>
            <Slider
              type="range"
              min="0"
              max="100"
              value={settings.alertVolume * 100}
              onChange={handleAlertVolumeChange}
            />
          </SettingItem>
        </Section>

        <Section>
          <SectionTitle>其他设置</SectionTitle>
          <Button onClick={() => onSettingsChange({ isCompact: !settings.isCompact })}>
            {settings.isCompact ? '切换到展开视图' : '切换到紧凑视图'}
          </Button>
          <ResetButton onClick={onReset}>
            重置所有设置
          </ResetButton>
        </Section>
      </Drawer>
    </>
  );
};
