import React, { useState, useCallback, useEffect } from 'react';
import styled from 'styled-components';
import { useAudioMonitor } from '../hooks/useAudioMonitor';
import { AlertNotification } from './AlertNotification';
import { VolumeVisualizer } from './VolumeVisualizer';
import { VolumeAnalysis } from './VolumeAnalysis';
import { CalibrationModal } from './CalibrationModal';
import { TestPanel } from './TestPanel';
import { SettingsPanel } from './SettingsPanel';
import { alertManager } from '../utils/alertManager';
import { volumeAnalyzer } from '../utils/volumeAnalyzer';
import { alertSound } from '../utils/alertSound';
import { settingsManager } from '../utils/settingsManager';
import type { Settings, AnalysisResult } from '../types';

const Container = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
`;

export const AudioTest: React.FC = () => {
  const [threshold, setThreshold] = useState(30);
  const [volumeState, setVolumeState] = useState<'normal' | 'warning'>('normal');
  const [alert, setAlert] = useState<{
    visible: boolean;
    message: string;
    type: 'warning' | 'error' | 'info';
  }>({
    visible: false,
    message: '',
    type: 'info'
  });

  const [analysis, setAnalysis] = useState<AnalysisResult>({
    currentStats: {
      min: 0,
      max: 0,
      average: 0,
      samples: [],
      timestamp: Date.now()
    },
    trend: 'stable'
  });

  const [isCalibrating, setIsCalibrating] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [settings, setSettings] = useState<Settings>(settingsManager.getSettings());
  const [error, setError] = useState<string | null>(null);

  const { volume, isMonitoring, startMonitoring, stopMonitoring } = useAudioMonitor({
    threshold,
    onVolumeLow: (volume) => {
      alertManager.handleVolume(volume, threshold);
    }
  });

  const handleAlertClose = useCallback(() => {
    setAlert(prev => ({ ...prev, visible: false }));
  }, []);

  const handleThresholdChange = useCallback((newThreshold: number) => {
    setThreshold(newThreshold);
    settingsManager.updateSettings({ threshold: newThreshold });
  }, []);

  const handleSettingsChange = useCallback((newSettings: Partial<Settings>) => {
    settingsManager.updateSettings(newSettings);
    setSettings(settingsManager.getSettings());
  }, []);

  useEffect(() => {
    alertManager.updateOptions({
      textEnabled: settings.textAlerts,
      soundEnabled: settings.soundAlerts,
      minInterval: settings.alertInterval,
      threshold: threshold
    });

    alertManager.setCallbacks({
      onVisualAlert: (isLow) => {
        setVolumeState(isLow ? 'warning' : 'normal');
      },
      onTextAlert: (message, type) => {
        setAlert({
          visible: true,
          message,
          type: type as 'warning' | 'error' | 'info'
        });
      }
    });

    return () => {
      alertManager.reset();
    };
  }, [settings, threshold]);

  const handleTest = useCallback((type: string) => {
    switch (type) {
      case 'lowVolume':
        let count = 0;
        const interval = setInterval(() => {
          if (count++ < 50) {
            volumeAnalyzer.addSample(15, threshold);
            alertManager.handleVolume(15, threshold);
          } else {
            clearInterval(interval);
          }
        }, 100);
        break;

      case 'normalVolume':
        volumeAnalyzer.addSample(50, threshold);
        alertManager.handleVolume(50, threshold);
        break;

      case 'volumeSpike':
        for (let i = 0; i < 10; i++) {
          setTimeout(() => {
            const volume = i % 2 ? 90 : 20;
            volumeAnalyzer.addSample(volume, threshold);
            alertManager.handleVolume(volume, threshold);
          }, i * 100);
        }
        break;

      case 'visualAlert':
        alertManager.handleVolume(20, threshold);
        break;

      case 'textAlert':
        setAlert({
          visible: true,
          message: '这是一条测试文字提示',
          type: 'info'
        });
        break;

      case 'soundAlert':
        alertSound.playAlert(440);
        break;

      case 'allAlerts':
        alertManager.handleVolume(10, threshold);
        alertSound.playAlert(880);
        setAlert({
          visible: true,
          message: '触发了所有类型的提示',
          type: 'warning'
        });
        break;

      case 'microphoneError':
        setError('麦克风访问被拒绝或未找到可用的音频输入设备');
        break;

      case 'connectionError':
        setError('音频处理服务连接失败');
        break;
    }
  }, [threshold]);

  const requestMicrophoneAccess = async () => {
    try {
      await navigator.mediaDevices.getUserMedia({ audio: true });
      // 授权成功，开始监控
      startMonitoring();
    } catch (error) {
      setError('请允许访问麦克风以开始音量监控');
    }
  };

  return (
    <Container>
      <AlertNotification
        isVisible={alert.visible}
        message={alert.message}
        type={alert.type}
        onClose={handleAlertClose}
      />

      <VolumeVisualizer
        volume={volume}
        threshold={threshold}
        isCompact={settings.compactMode}
      />

      <VolumeAnalysis
        analysis={analysis}
        isCompact={settings.compactMode}
      />

      <TestPanel
        onTest={handleTest}
        currentVolume={volume}
        threshold={threshold}
      />

      <SettingsPanel
        isOpen={isSettingsOpen}
        settings={settings}
        onClose={() => setIsSettingsOpen(false)}
        onChange={handleSettingsChange}
      />

      <CalibrationModal
        isOpen={isCalibrating}
        onClose={() => setIsCalibrating(false)}
        onComplete={(newThreshold) => {
          setThreshold(newThreshold);
          setIsCalibrating(false);
        }}
      />

      {error && (
        <ErrorMessage>
          {error}
          <Button onClick={requestMicrophoneAccess}>重试</Button>
        </ErrorMessage>
      )}
    </Container>
  );
};
