export class AudioContextManager {
  private audioContext: AudioContext | null = null;
  private mediaStream: MediaStream | null = null;
  private analyser: AnalyserNode | null = null;
  private dataArray: Uint8Array | null = null;
  private isInitialized: boolean = false;
  private initializationPromise: Promise<void> | null = null;
  private eventListeners: { type: string; listener: EventListener }[] = [];

  constructor() {
    this.addUserInteractionListener();
  }

  private addUserInteractionListener() {
    const userInteractionEvents = ['click', 'touchstart', 'keydown'];
    const handler = async () => {
      if (this.audioContext) {
        try {
          await this.audioContext.resume();
          // 移除所有事件监听器
          this.removeAllEventListeners();
        } catch (error) {
          console.error('Error resuming AudioContext:', error);
        }
      }
    };

    userInteractionEvents.forEach(eventType => {
      const listener = handler.bind(this);
      document.addEventListener(eventType, listener, { once: true });
      this.eventListeners.push({ type: eventType, listener });
    });
  }

  private removeAllEventListeners() {
    this.eventListeners.forEach(({ type, listener }) => {
      document.removeEventListener(type, listener);
    });
    this.eventListeners = [];
  }

  private async initialize() {
    if (this.initializationPromise) {
      return this.initializationPromise;
    }

    this.initializationPromise = new Promise<void>(async (resolve, reject) => {
      try {
        if (!this.audioContext) {
          this.audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
        }

        if (this.audioContext.state !== 'running') {
          await this.audioContext.resume();
        }

        this.mediaStream = await navigator.mediaDevices.getUserMedia({
          audio: {
            echoCancellation: true,
            noiseSuppression: true,
            autoGainControl: true
          },
          video: false
        });

        const source = this.audioContext.createMediaStreamSource(this.mediaStream);
        this.analyser = this.audioContext.createAnalyser();
        this.analyser.fftSize = 2048;
        source.connect(this.analyser);
        this.dataArray = new Uint8Array(this.analyser.frequencyBinCount);
        
        this.isInitialized = true;
        resolve();
      } catch (error) {
        this.cleanup();
        reject(error);
      }
    });

    return this.initializationPromise;
  }

  private cleanup() {
    this.removeAllEventListeners();
    
    if (this.mediaStream) {
      this.mediaStream.getTracks().forEach(track => track.stop());
      this.mediaStream = null;
    }
    if (this.analyser) {
      this.analyser.disconnect();
      this.analyser = null;
    }
    if (this.audioContext) {
      this.audioContext.close().catch(console.error);
      this.audioContext = null;
    }
    this.dataArray = null;
    this.isInitialized = false;
    this.initializationPromise = null;
  }

  async startMonitoring() {
    try {
      // 添加用户提示
      console.log('请点击页面任意位置以启动音频监控');
      await this.initialize();
    } catch (error) {
      this.cleanup();
      throw new Error('Failed to initialize audio context: ' + (error as Error).message);
    }
  }

  stopMonitoring() {
    this.cleanup();
  }

  getVolume(): number {
    if (!this.analyser || !this.dataArray || !this.isInitialized) {
      return 0;
    }

    try {
      // 获取音频数据
      this.analyser.getByteFrequencyData(this.dataArray);

      // 计算平均音量
      const average = this.dataArray.reduce((acc, value) => acc + value, 0) / this.dataArray.length;
      
      // 将音量值映射到 0-100 范围
      return Math.min(Math.round((average / 255) * 100), 100);
    } catch (error) {
      console.error('Error getting volume:', error);
      return 0;
    }
  }

  isActive(): boolean {
    return this.isInitialized && this.audioContext !== null && this.audioContext.state === 'running';
  }
}

export const audioContextManager = new AudioContextManager();
